                    pStm->BgnTrans( pContext, S812::GetInstance() );
                    ((MainStm*)pStm)->m_S8_TopS8_Region2.Reset(pContext, pStm, S8_Region2::S832::GetInstance());
                    pStm->EndTrans( pContext );
